import React, { useState, useEffect } from "react";
import Card from "./Card";
import "../App.css";

export default function Category(props) {
  const [categoryCity, setCategoryCity] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  useEffect(() => {
    if (props.selectCity === "Select City") return;
    async function fetchMyAPI() {
      let response = await fetch(
        `https://mini-london-guide-fullstack-challange.onrender.com/${props.selectCity}`
      );
      response = await response.json();
      setCategoryCity(response);
    }

    fetchMyAPI().catch(console.error);
  }, [props.selectCity]);
  return (
    <div
      className="btn-group"
      role="group"
      aria-label="Basic radio toggle button group"
    >
      {categoryCity.map((elmCategory, index) => (
        <div key={index}>
          <label
            className="btn btn-outline-primary"
            htmlFor={index}
            key={index + 1000}
          >
            {elmCategory}
          </label>
          <input
            type="radio"
            className="btn-check"
            name="categoryCity"
            id={index}
            key={index + 2000}
            value={elmCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          />
        </div>
      ))}
      if(!selectedCategory)? <Card selectCity={selectCity} selectedCategory />
    </div>
  );
}

